const auto cols = 31;
const auto rows = 323;

const auto right = 3;
const auto down = 1;

int main(int argc, char const *argv[])
{
	int open = 0;
	int tree = 0;

	int col = 0;
	int row = 0;

	do
	{
		const auto pos = col + (row * cols);

		if (map[pos] == '.')
			++open;
		else if (map[pos] == '#')
			++tree;
		else
			std::cerr << "WTF!";

		// move down the slope
		col += right;
		row += down;

		// move to the correct point on the map
		if (col >= cols)
			col -= cols;
	}
	while ( row < rows );

	std::cout << "open: " << open << " trees: " << tree << std::endl;

	return 0;
}
